import assert from 'assert';
import reaUtils from '../lib';

describe('rea-utils', function () {
  it('should be a factory function', function () {
    assert(typeof reaUtils === 'function');
  });

  let util = reaUtils();
  describe('#version', function () {
    it('should be defined', function () {
      assert(util.version);
    });
  });

  describe('#arguments', function () {
    it('should not be null', function () {
      assert(util.arguments,
        'Empty arguments should return empty object `{}`');
    });
    it('should accept null assignment', function () {
      util.arguments = null;
      assert(true,
        'Assignment to null should not throw exceptions');
    });

    it('should support multiple *.rea files at the same time', function () {
      var arr = ['service.rea'];
      util.arguments = { _: arr };
      assert(util.inputFilePaths.length === arr.length);
    });
  });

  describe('#run', function () {
    it('should fail when input file does not exist', function () {
      var arr = ['non-existing-file.rea'];
      util.arguments = { _: arr };
      util.run({
        done: assert.bind(null, false, 'util.run should not call done'),
        fail: assert.bind(null, true, 'util.run should call fail()')
      });
    });

    it('should run when input file is valid', function (done) {
      var arr = ['service.rea'];
      util.arguments = { _: arr };
      util.run({done: done.bind(null, null)});
    });
  });

});

