import assert from 'assert';
import relationshipBuilder from '../lib/relationship-builder.js';

describe('relationship-builder', function () {
  it('should be a factory function', function () {
    assert(typeof relationshipBuilder === 'function');
  });

  let builder = relationshipBuilder();
  describe('#text', function () {
    it('should have text properties', function () {
      assert(builder.text);
    });
  });
  describe('#build', function () {
    it('should return empty array when text is empty or null', function () {
      var inputs = [null, '', ' ', `
      `];
      inputs.forEach((input, index) => {
        builder.text = input;
        var relationships = builder.build();
        assert(relationships.length === 0, `Failed input #${index}: '${input}'`);
      });
    });
    it('should return 1 relationship when text contains only 1 valid line', function () {
      var inputs = ['RE:[table 1](0,1)-[table 2](1,*)'];
      inputs.forEach((input, index) => {
        let inputDesc = `Failed input #${index}: '${input}'`;

        builder.text = input;
        let relationships = builder.build();
        assert.equal(relationships.length, 1, `Should return only 1 relationship. ${inputDesc}`);

        var relationship = relationships.pop();
        assert.equal(relationship.captures.length, 7 /* fragments */ + /* entire line */ 1, `Should capture 8 groups. ${inputDesc}`);
        assert.equal(relationship.type, 'RE', `Should be Resource-Event relationship. ${inputDesc}`);
        assert.equal(relationship.table1, 'table 1', `Should capture first table name. ${inputDesc}`);
        assert.equal(relationship.table2, 'table 2', `Should capture second table name. ${inputDesc}`);
        assert.equal(relationship.min1, '0', `Should capture first min. ${inputDesc}`);
        assert.equal(relationship.max1, '1', `Should capture first max. ${inputDesc}`);
        assert.equal(relationship.min2, '1', `Should capture second min. ${inputDesc}`);
        assert.equal(relationship.max2, '*', `Should capture second max. ${inputDesc}`);
      });
    });
  });
});

