import assert from 'assert';
import tableCreator from '../lib/table-creator.js';

describe('table-creator', function() {
  it('should be a factory function', function() {
    assert(typeof tableCreator === 'function');
  });

  let creator = tableCreator();
  describe('#createTables', function() {
    it('should have createTables function', function() {
      assert(creator.createTables);
    });
    it('should create 2 tables', function() {
      let manyToOneRB = {
        build: function() {
          return [
            {
              // each Import can produce 0 or many drafts
              // each Draft can be produced by 0 or at most 1 Import
              //
              // Resource                                       ---------->     Event
              // Draft table should have a nullable foreign key referencing the Import table
              type: 'RE',
              table1: 'Draft',
              min1: '0',
              max1: '*',
              table2: 'Import',
              min2: '0',
              max2: '1'
            }
          ];
        }
      };
      var tables = creator.createTables(manyToOneRB);
      assert.equal(Object.keys(tables).length, 2);
    });
  });
});

